import React, { useState } from 'react';
import Info from './Info';

const App = () => {
	const [visible, setVisible] = useState(false);

	return (
		<div>
			<h2>8장. Hooks</h2>
			<button
				onClick={() => {
					setVisible(!visible);
				}}
			>
				{visible ? '숨기기' : '보이기'}
			</button>
			<hr />
			{visible && <Info />}
		</div>
	);
};

export default App;
