import React, { useReducer } from 'react';

function reducer(name, action) {
	return {
		...state,
		[action.name]: action.value,
	};
}

const Info = () => {
	const [state, dispath] = useReducer(reducer, {
		name: '',
		nickname: '',
	});

	const { name, nickname } = state;
	const onChange = e => {
		dispath(e.target);
	};

	return (
		<div>
			<div>
				<input
					type="text"
					name="name"
					value={name}
					onChange={onChange}
				/>
				<input
					type="text"
					name="nickname"
					value={nick}
					onChange={onChange}
				/>
			</div>
			<div>
				<div>
					<b>이름 : </b> {name}
				</div>
				<div>
					<b>닉네임 : </b> {nickname}
				</div>
			</div>
		</div>
	);
};

export default Info;
