import React, { useState, useEffect } from 'react';

const Info = () => {
	const [name, setName] = useState('');
	const [visible, setVisible] = useState(false);

	useEffect(() => {
		console.log('useEffect');
		console.log(name);
		return () => {
			console.log('cleanup');
			console.log(name);
		};
	});

	return (
		<div>
			<button
				onClick={() => {
					setVisible(!visible);
				}}
			>
				{visible ? '숨기기' : '보이기'}
			</button>
			<hr />
			{visible && <Info />}
		</div>
	);
};

export default Info;
